<?php require_once('PelEntryNumber.php'); class PelEntryByte extends PelEntryNumber { function __construct($tag ) { $this->tag = $tag; $this->min = 0; $this->max = 255; $this->format = PelFormat::BYTE; $value = func_get_args(); array_shift($value); $this->setValueArray($value); } function numberToBytes($number, $order) { return chr($number); } } class PelEntrySByte extends PelEntryNumber { function __construct($tag ) { $this->tag = $tag; $this->min = -128; $this->max = 127; $this->format = PelFormat::SBYTE; $value = func_get_args(); array_shift($value); $this->setValueArray($value); } function numberToBytes($number, $order) { return chr($number); } } class PelEntryWindowsString extends PelEntry { private $str; function __construct($tag, $str = '') { $this->tag = $tag; $this->format = PelFormat::BYTE; $this->setValue($str); } function setValue($str) { $l = strlen($str); $this->components = 2 * ($l + 1); $this->str = $str; $this->bytes = ''; for ($i = 0; $i < $l; $i++) $this->bytes .= $str{$i} . chr(0x00); $this->bytes .= chr(0x00) . chr(0x00); } function getValue() { return $this->str; } function getText($brief = false) { return $this->str; } } ?>