<?php require_once('PelJpegContent.php'); class PelJpegComment extends PelJpegContent { private $comment = ''; function __construct($comment = '') { $this->comment = $comment; } function load(PelDataWindow $d) { $this->comment = $d->getBytes(); } function setValue($comment) { $this->comment = $comment; } function getValue() { return $this->comment; } function getBytes() { $this->comment; } function __toString() { return $this->getValue(); } } ?>