<?php class PelFormat { const BYTE = 1; const ASCII = 2; const SHORT = 3; const LONG = 4; const RATIONAL = 5; const SBYTE = 6; const UNDEFINED = 7; const SSHORT = 8; const SLONG = 9; const SRATIONAL = 10; const FLOAT = 11; const DOUBLE = 12; static function getName($type) { switch ($type) { case self::ASCII: return 'Ascii'; case self::BYTE: return 'Byte'; case self::SHORT: return 'Short'; case self::LONG: return 'Long'; case self::RATIONAL: return 'Rational'; case self::SBYTE: return 'SByte'; case self::SSHORT: return 'SShort'; case self::SLONG: return 'SLong'; case self::SRATIONAL: return 'SRational'; case self::FLOAT: return 'Float'; case self::DOUBLE: return 'Double'; case self::UNDEFINED: return 'Undefined'; default: return Pel::fmt('Unknown format: 0x%X', $type); } } static function getSize($type) { switch ($type) { case self::ASCII: return 1; case self::BYTE: return 1; case self::SHORT: return 2; case self::LONG: return 4; case self::RATIONAL: return 8; case self::SBYTE: return 1; case self::SSHORT: return 2; case self::SLONG: return 4; case self::SRATIONAL: return 8; case self::FLOAT: return 4; case self::DOUBLE: return 8; case self::UNDEFINED: return 1; default: return Pel::fmt('Unknown format: 0x%X', $type); } } } ?>